import React, { useRef, useState, useEffect } from "react";
import { faCheck, faTimes } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";
import axios from "axios";

const USER_REGEX = /^[a-zA-Z][a-zA-Z0-9-_]{3,23}$/;
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;
const REGISTER_URL = "/register";

const validationSchema = Yup.object({
  email: Yup.string().email("Invalid email format").required("Required"),
  password: Yup.string()
    .matches(PWD_REGEX, "Password must be 8-24 characters, include uppercase, lowercase, number & special character.")
    .required(""),
});

const LoginPage = () => {
  const userRef = useRef();
  const errRef = useRef();

  const [errMsg, setErrMsg] = useState("");
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    userRef.current?.focus();
  }, []);

  return (
    <Formik
      initialValues={{ email: "", password: "" }}
      validationSchema={validationSchema}
      onSubmit={async (values, { setSubmitting }) => {
        try {
          const response = await axios.post(
            REGISTER_URL,
            { user: values.email, pwd: values.password },
            { headers: { "Content-Type": "application/json" }, withCredentials: true }
          );
          console.log(response.data);
          setSuccess(true);
        } catch (err) {
          if (!err?.response) setErrMsg("No Server Response");
          else if (err.response?.status === 409) setErrMsg("Username Taken");
          else setErrMsg("Registration Failed");
          errRef.current?.focus();
        }
        setSubmitting(false);
      }}
    >
      {({ isSubmitting }) =>
        success ? (
          <section>
            <h1>Success!</h1>
            <p>
              <a href="#">Sign In</a>
            </p>
          </section>
        ) : (
          <section>
            <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">
              {errMsg}
            </p>
            <Form>
              <center>
                <h1>Login</h1>
              </center>

              <label htmlFor="email">
                Username or Email:
                <Field name="email" type="email" placeholder="Enter Username or Email" innerRef={userRef} />
                <ErrorMessage name="email" component="div" className="error" />
              </label>
              <div className="remember-forgot">
                <a href="http://localhost:3002/forgot-username">Forgot Username?</a>
              </div>

              <label htmlFor="password">
                Password:
                <Field name="password" type="password" placeholder="Enter password" />
                <ErrorMessage name="password" component="div" className="error" />
              </label>

              <div className="remember-forgot">
                <label>
                  <input type="checkbox" name="rememberMe" />Remember me
                </label>
                <a href="http://localhost:3002/forgot-password">Forgot Password?</a>
              </div>

              <button type="submit" disabled={isSubmitting}>
                {isSubmitting ? "Logging in..." : "Login"}
              </button>

              <div className="register-link">
                <p>
                  Don't have an account? <a href="http://localhost:3002/register">Register</a>
                </p>
              </div>
            </Form>
          </section>
        )
      }
    </Formik>
  );
};

export default LoginPage;
