import React, { useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as Yup from "yup";
import axios from "axios";
import "../styles/PersonalInfo.css";
import logo1 from "../assets/leftlogo.png"; 
import logo2 from "../assets/rightlogo.png"; 
import farmImage from "../assets/farmImage.png";

// ✅ Validation Schema using Yup
const validationSchema = Yup.object().shape({
  firstName: Yup.string().required("First Name is required"),
  middleName: Yup.string().required("Middle Name is required"),
  lastName: Yup.string().required("Last Name is required"),
  gender: Yup.string().required("Gender is required"),
  salutation: Yup.array()
    .min(1, "Select at least one salutation")
    .required("Salutation is required"),
  nationality: Yup.string().required("Nationality is required"),
  dob: Yup.string()
    .required("Date of Birth is required")
    .matches(/^\d{2}\/\d{2}\/\d{4}$/, "DOB must be in DD/MM/YYYY format"),
});

const PersonalInfo = () => {
  // ✅ React Hook Form
  const {
    register,
    handleSubmit,
    setValue,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(validationSchema),
  });

  // ✅ Fetch Default User Data (Simulating API Call)
  useEffect(() => {
    axios.get("https://api.example.com/user/1")
      .then(response => {
        const userData = response.data;
        setValue("firstName", userData.firstName);
        setValue("middleName", userData.middleName);
        setValue("lastName", userData.lastName);
        setValue("gender", userData.gender);
        setValue("nationality", userData.nationality);
        setValue("dob", userData.dob);
      })
      .catch(error => console.error("Error fetching user data:", error));
  }, [setValue]);

  // ✅ Form Submission
  const onSubmit = async (data) => {
    console.log("Form Data: ", data);
    
    try {
      const response = await axios.post("https://api.example.com/user/update", data);
      alert("Form submitted successfully!");
      console.log(response.data);
    } catch (error) {
      console.error("Error submitting form:", error);
      alert("Failed to submit form.");
    }
  };

  return (
    <div className="logo-container">
      
      <header className="top-bar">
        <img src={logo1} alt="Digital Agristack Logo" className="logo-left" />
        <img src={logo2} alt="DATE Logo" className="logo-right" />
      </header>

      <div className="middle-container">
        <nav className="nav-links">
          <a href="#">📌 Address</a>
          <a href="#">📑 Professional Information</a>
          <a href="#">🌱 Current Crop Information</a>
        </nav>
      </div>

      <div className="main-frame">
        
        <form className="form-section" onSubmit={handleSubmit(onSubmit)}>
          <h2>Personal Information</h2>
          
          <div className="form-grid">
  {/* Left Column */}
       <div className="form-column left">
    
    {/* Photo Upload */}
      <div className="form-group photo-group">
        <label>Photo <span className="required">*</span></label>
        <div className="photo-box">Photo</div>
    </div>

    {/* Name Fields */}
     <div className="form-group">
       <label>First Name <span className="required">*</span></label>
       <input type="text" {...register("firstName")} readOnly />
       <p className="error">{errors.firstName?.message}</p>
     </div>

     <div className="form-group">
       <label>Middle Name <span className="required">*</span></label>
       <input type="text" {...register("middleName")} readOnly />
       <p className="error">{errors.middleName?.message}</p>
     </div>

     <div className="form-group">
      <label>Last Name <span className="required">*</span></label>
      <input type="text" {...register("lastName")} readOnly />
      <p className="error">{errors.lastName?.message}</p>
     </div>

     </div>

  {/* Right Column */}
     <div className="form-column right">

    {/* Gender */}
        <div className="form-group">
           <label>Gender <span className="required">*</span></label>
            <select {...register("gender")}>
              <option value="">Select</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
              <option value="Transgender">Transgender</option>
        </select>
          <p className="error">{errors.gender?.message}</p>
        </div>

    {/* Salutation */}
       <div className="form-group">
         <label>Salutation <span className="required">*</span></label>
          <div className="salutation-options">
          <label><input type="checkbox" value="Mr." {...register("salutation")} /> Mr.</label>
          <label><input type="checkbox" value="Mrs." {...register("salutation")} /> Mrs.</label>
          <label><input type="checkbox" value="Ms." {...register("salutation")} /> Ms.</label>
          <label><input type="checkbox" value="Miss." {...register("salutation")} /> Miss.</label>
          <label><input type="checkbox" value="Dr." {...register("salutation")} /> Dr.</label>
       </div>
       <p className="error">{errors.salutation?.message}</p>
       </div>

