import React, { useState, useEffect } from "react";
import { useForm, FormProvider } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as Yup from "yup";
import axios from "axios";
import farmImage from "../assets/farmImage.png";
import "../styles/Farmerform.css";

const steps = [
  "Personal Information",
  "Address",
  "Professional Information",
  "Current Crop",
  "Proposed Crop",
  "Irrigation Details",
  "Other Information",
];

const validationSchemas = [
    Yup.object().shape({
      firstName: Yup.string().required("First Name is required"),
      middleName: Yup.string().required("Middle Name is required"),
      lastName: Yup.string().required("Last Name is required"),
      gender: Yup.string().required("Gender is required"),
      salutation: Yup.string().required("Salutation is required"),
      nationality: Yup.string().required("Nationality is required"),
      dob: Yup.string()
        .required("Date of Birth is required")
        .matches(/^[0-9]{2}\/[0-9]{2}\/[0-9]{4}$/, "DOB must be in DD/MM/YYYY format"),
      fatherName: Yup.string().required("Father Name is required"),
      alternativeNumber: Yup.string()
        .matches(/^[0-9]{10}$/, "Enter a valid 10-digit number")
        .required("Alternative Number is required"),
      alternativeType: Yup.string().required("Alternative No. Type is required"),
    }),
    Yup.object().shape({
      addressLine1: Yup.string().required("Address Line 1 is required"),
      state: Yup.string().required("State is required"),
      district: Yup.string().required("District is required"),
      pincode: Yup.string().required("Pincode is required"),
    }),
    Yup.object().shape({
      occupation: Yup.string().required("Occupation is required"),
      experience: Yup.string().required("Experience is required"),
    }),
    Yup.object().shape({
      cropName: Yup.string().required("Current Crop is required"),
      area: Yup.string().required("Area is required"),
    }),
    Yup.object().shape({
      proposedCrop: Yup.string().required("Proposed Crop is required"),
      season: Yup.string().required("Season is required"),
    }),
    Yup.object().shape({
      irrigationType: Yup.string().required("Irrigation Type is required"),
      waterSource: Yup.string().required("Water Source is required"),
    }),
    Yup.object().shape({
      additionalInfo: Yup.string().required("This field is required"),
    }),
  ]; 

const FarmerForm = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const methods = useForm({
    resolver: yupResolver(validationSchemas[currentStep]),
    mode: "onBlur",
  });

  const { register, handleSubmit, trigger, formState: { errors }, setValue } = methods;

  const onSubmit = async (data) => {
    if (currentStep < steps.length - 1) {
      const valid = await trigger();
      if (valid) setCurrentStep((prev) => prev + 1);
    } else {
      alert("Submitting full form...");
      console.log("Form submitted:", data);
    }
  };

  const handlePrev = () => {
    setCurrentStep((prev) => prev - 1);
  };

  useEffect(() => {
    if (currentStep === 0) {
      axios.get("https://api.example.com/user/1")
        .then((res) => {
          const fields = Object.keys(res.data);
          fields.forEach((field) => setValue(field, res.data[field]));
        })
        .catch((err) => console.error("Data fetch error:", err));
    }
  }, [currentStep, setValue]);

      const [image, setImage] = useState(null);
      
        // Function to handle file selection
        const handleImageUpload = (event) => {
          const file = event.target.files[0];
          if (file) {
            const imageUrl = URL.createObjectURL(file);
            setImage(imageUrl);
          }
        };


  return (
    <div className="form-wrapper">
      <div className="form-left">
        <h2>{steps[currentStep]}</h2>
        <FormProvider {...methods}>
          <form onSubmit={handleSubmit(onSubmit)}>
            {currentStep === 0 && (
              <>
               <div className="form-section">
  
  <div className="form-content">
    {/* Left Column */}
    <div className="form-column">
    <div className="form-group">
            <label>Photo <span className="required">*</span></label>
            <div className="photo-box">
             {image ? (
             <img src={image} alt="Uploaded Farmer" className="uploaded-photo" />
            ) : (
              <p>Upload Farmer Photo</p>
            )}
          </div>
            <input 
              type="file" 
              accept="image/*" 
              onChange={handleImageUpload} 
              className="photo-input"
            />
          </div>

      <div className="form-group">
        <label>First Name<span className="required">*</span></label>
        <input type="text" {...register("firstName")} />
      </div>

      <div className="form-group">
        <label>Middle Name<span className="required">*</span></label>
        <input type="text" {...register("middleName")} />
      </div>

      <div className="form-group">
        <label>Last Name<span className="required">*</span></label>
        <input type="text" {...register("lastName")} />
      </div>

      <div className="form-group">
        <label>Gender<span className="required">*</span></label>
        <select {...register("gender")}>
          <option value="">Select</option>
          <option value="Male">Male</option>
          <option value="Female">Female</option>
          <option value="Transgender">Transgender</option>
        </select>
      </div>
    </div>

    {/* Right Column */}
    <div className="form-column">
      <div className="form-group">
        
        <label>Salutation <span className="required">*</span>
                  <select {...register("salutation")}>
                    <option value="">Select Salutation</option>
                    <option value="D/O">D/O</option>
                    <option value="S/O">S/O</option>
                    <option value="W/O">W/O</option>
                  </select>
                </label>
                <p>{errors.salutation?.message}</p>
      </div>

      <div className="form-group">
        <label>Nationality<span className="required">*</span></label>
        <select {...register("nationality")}>
          <option value="">Select</option>
          <option value="Indian">Indian</option>
        </select>
      </div>

      <div className="form-group">
        <label>DOB<span className="required">*</span></label>
        <input type="text" placeholder="DD/MM/YYYY" {...register("dob")} />
      </div>

      <div className="form-group">
        <label>Father Name<span className="required">*</span></label>
        <input type="text" {...register("fatherName")} />
      </div>

      <div className="form-group">
        <label>Alternative Number<span className="required">*</span></label>
        <input type="text" {...register("alternativeNumber")} />
      </div>

      <div className="form-group">
        <label>Alternative No. Type<span className="required">*</span></label>
        <input type="text" {...register("alternativeType")} />
      </div>
    </div>
  </div>
</div>

              </>
            )}

            {currentStep === 2 && (
              <>
                <label>Occupation <span className="required">*</span>
                  <input {...register("occupation")} />
                </label>
                <p>{errors.occupation?.message}</p>

                <label>Experience <span className="required">*</span>
                  <input {...register("experience")} />
                </label>
                <p>{errors.experience?.message}</p>
              </>
            )}

            {currentStep === 3 && (
              <>
                <label>Current Crop <span className="required">*</span>
                  <input {...register("cropName")} />
                </label>
                <p>{errors.cropName?.message}</p>

                <label>Area <span className="required">*</span>
                  <input {...register("area")} />
                </label>
                <p>{errors.area?.message}</p>
              </>
            )}

            {currentStep === 4 && (
              <>
                <label>Proposed Crop <span className="required">*</span>
                  <input {...register("proposedCrop")} />
                </label>
                <p>{errors.proposedCrop?.message}</p>

                <label>Season <span className="required">*</span>
                  <input {...register("season")} />
                </label>
                <p>{errors.season?.message}</p>
              </>
            )}

            {currentStep === 5 && (
              <>
                <label>Irrigation Type <span className="required">*</span>
                  <input {...register("irrigationType")} />
                </label>
                <p>{errors.irrigationType?.message}</p>

                <label>Water Source <span className="required">*</span>
                  <input {...register("waterSource")} />
                </label>
                <p>{errors.waterSource?.message}</p>
              </>
            )}

            {currentStep === 6 && (
              <>
                <label>Additional Information <span className="required">*</span>
                  <textarea {...register("additionalInfo")} />
                </label>
                <p>{errors.additionalInfo?.message}</p>
              </>
            )}

            <div className="btn-group">
              {currentStep > 0 && <button type="button" onClick={handlePrev}>Previous</button>}
              <button type="submit">{currentStep === steps.length - 1 ? "Submit" : "Next"}</button>
            </div>
          </form>
        </FormProvider>
      </div>
      <div className="form-right">
        <img src={farmImage} alt="Farm Field" className="form-image" />
      </div>
    </div>
  );
};

export default FarmerForm;
