import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

const LoginPage = () => {
  const validationSchema = Yup.object({
    email: Yup.string().email('Invalid email format').required('Required'),
    password: Yup.string().required('Required'),
  });

  return (
    <Formik
      initialValues={{ email: '', password: '' }}
      validationSchema={validationSchema}
      onSubmit={(values) => {
        console.log(values);
      }}
    >
      <Form>
        <h1>Login</h1>
        <div>
          <label htmlFor="email">Email</label>
          <Field name="email" placeholder="Enter Username or Email" type="email" />
          <ErrorMessage name="email" component="div" />
        </div>
        <div className="remember-forgot">
            <a href="http://localhost:3002/forgot-username">Forgot Username?</a>
        </div>
        <div>
          <label htmlFor="password">Password</label>
          <Field name="password" placeholder="Enter password" type="password" />
          <ErrorMessage name="password" component="div" />
        </div>
        <div className="remember-forgot">
        <input type="checkbox" />
          <label>
           Remember me
          </label>
            <a href="http://localhost:3002/forgot-password">Forgot Password?</a>
        </div>
        <button type="submit">Login</button>
        <div className="register-link">
          <p>Don't have an account? <a href="http://localhost:3002/register">Register</a></p>
        </div>
      </Form>
    </Formik>
  );
};

export default LoginPage;
