import React, { useState, useEffect } from "react";
import { useForm, FormProvider } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as Yup from "yup";
import axios from "axios";
import "../styles/Farmerform.css"


const steps = [
  "Personal Information",
  "Address",
  "Professional Information",
  "Current Crop",
  "Proposed Crop",
  "Irrigation Details",
  "Other Information",
];

const validationSchemas = [
  Yup.object().shape({
    firstName: Yup.string().required("First Name is required"),
    middleName: Yup.string().required("Middle Name is required"),
    lastName: Yup.string().required("Last Name is required"),
    gender: Yup.string().required("Gender is required"),
    salutation: Yup.string().required("Salutation is required"),
    nationality: Yup.string().required("Nationality is required"),
    dob: Yup.string()
      .required("Date of Birth is required")
      .matches(/^[0-9]{2}\/[0-9]{2}\/[0-9]{4}$/, "DOB must be in DD/MM/YYYY format"),
    fatherName: Yup.string().required("Father Name is required"),
    alternativeNumber: Yup.string()
      .matches(/^[0-9]{10}$/, "Enter a valid 10-digit number")
      .required("Alternative Number is required"),
    alternativeType: Yup.string().required("Alternative No. Type is required"),
  }),
  Yup.object().shape({
    addressLine1: Yup.string().required("Address Line 1 is required"),
    state: Yup.string().required("State is required"),
    district: Yup.string().required("District is required"),
    pincode: Yup.string().required("Pincode is required"),
  }),
  Yup.object().shape({
    occupation: Yup.string().required("Occupation is required"),
    experience: Yup.string().required("Experience is required"),
  }),
  Yup.object().shape({
    cropName: Yup.string().required("Current Crop is required"),
    area: Yup.string().required("Area is required"),
  }),
  Yup.object().shape({
    proposedCrop: Yup.string().required("Proposed Crop is required"),
    season: Yup.string().required("Season is required"),
  }),
  Yup.object().shape({
    irrigationType: Yup.string().required("Irrigation Type is required"),
    waterSource: Yup.string().required("Water Source is required"),
  }),
  Yup.object().shape({
    additionalInfo: Yup.string().required("This field is required"),
  }),
];

const FarmerForm = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const methods = useForm({
    resolver: yupResolver(validationSchemas[currentStep]),
    mode: "onBlur",
  });

  const { register, handleSubmit, trigger, formState: { errors }, setValue } = methods;

  const onSubmit = async (data) => {
    if (currentStep < steps.length - 1) {
      const valid = await trigger();
      if (valid) setCurrentStep((prev) => prev + 1);
    } else {
      alert("Submitting full form...");
      console.log("Form submitted:", data);
    }
  };

  const handlePrev = () => {
    setCurrentStep((prev) => prev - 1);
  };

  useEffect(() => {
    if (currentStep === 0) {
      axios.get("https://api.example.com/user/1")
        .then((res) => {
          const fields = Object.keys(res.data);
          fields.forEach((field) => setValue(field, res.data[field]));
        })
        .catch((err) => console.error("Data fetch error:", err));
    }
  }, [currentStep, setValue]);

  return (
    <div className="form-container">
      <h2>{steps[currentStep]}</h2>
      <FormProvider {...methods}>
        <form onSubmit={handleSubmit(onSubmit)}>
          {currentStep === 0 && (
            <>
              <input placeholder="First Name" {...register("firstName")} />
              <p>{errors.firstName?.message}</p>

              <input placeholder="Middle Name" {...register("middleName")} />
              <p>{errors.middleName?.message}</p>

              <input placeholder="Last Name" {...register("lastName")} />
              <p>{errors.lastName?.message}</p>

              <select {...register("gender")}>
                <option value="">Select Gender</option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
                <option value="Transgender">Transgender</option>
              </select>
              <p>{errors.gender?.message}</p>

              <select {...register("salutation")}>
                   <option value="">Select Salutation</option>
                   <option value="D/O">D/O</option>
                   <option value="S/O">S/O</option>
                   <option value="W/O">W/O</option>
              </select>
              <p>{errors.salutation?.message}</p>

              <input placeholder="Nationality" {...register("nationality")} />
              <p>{errors.nationality?.message}</p>

              <input placeholder="DOB (DD/MM/YYYY)" {...register("dob")} />
              <p>{errors.dob?.message}</p>

              <input placeholder="Father Name" {...register("fatherName")} />
              <p>{errors.fatherName?.message}</p>

              <input placeholder="Alternative Number" {...register("alternativeNumber")} />
              <p>{errors.alternativeNumber?.message}</p>

              <input placeholder="Alternative Type" {...register("alternativeType")} />
              <p>{errors.alternativeType?.message}</p>
            </>
          )}

          {currentStep === 1 && (
            <>
              <input placeholder="Address Line 1" {...register("addressLine1")} />
              <p>{errors.addressLine1?.message}</p>

              <input placeholder="State" {...register("state")} />
              <p>{errors.state?.message}</p>

              <input placeholder="District" {...register("district")} />
              <p>{errors.district?.message}</p>

              <input placeholder="Pincode" {...register("pincode")} />
              <p>{errors.pincode?.message}</p>
            </>
          )}

          {currentStep === 2 && (
            <>
              <input placeholder="Occupation" {...register("occupation")} />
              <p>{errors.occupation?.message}</p>

              <input placeholder="Experience" {...register("experience")} />
              <p>{errors.experience?.message}</p>
            </>
          )}

          {currentStep === 3 && (
            <>
              <input placeholder="Current Crop" {...register("cropName")} />
              <p>{errors.cropName?.message}</p>

              <input placeholder="Area" {...register("area")} />
              <p>{errors.area?.message}</p>
            </>
          )}

          {currentStep === 4 && (
            <>
              <input placeholder="Proposed Crop" {...register("proposedCrop")} />
              <p>{errors.proposedCrop?.message}</p>

              <input placeholder="Season" {...register("season")} />
              <p>{errors.season?.message}</p>
            </>
          )}

          {currentStep === 5 && (
            <>
              <input placeholder="Irrigation Type" {...register("irrigationType")} />
              <p>{errors.irrigationType?.message}</p>

              <input placeholder="Water Source" {...register("waterSource")} />
              <p>{errors.waterSource?.message}</p>
            </>
          )}

          {currentStep === 6 && (
            <>
              <textarea placeholder="Additional Information" {...register("additionalInfo")} />
              <p>{errors.additionalInfo?.message}</p>
            </>
          )}

          <div className="btn-group">
            {currentStep > 0 && <button type="button" onClick={handlePrev}>Previous</button>}
            <button type="submit">{currentStep === steps.length - 1 ? "Submit" : "Next"}</button>
          </div>
        </form>
      </FormProvider>
    </div>
  );
};

export default FarmerForm;
